import React from 'react'
import PropTypes from 'prop-types'

const Input = ({
    label = '',
    name = '',
    type = 'text',
    className = '',
    inputClassName = '',
    isRequired = true,
    placeholder = '',
    value = '',
    onChange = () => {},
}) => {
  return (
    <div className={`${className}`}>
        <label htmlFor={name} className="block text-sm font-medium text-gray-800">
            {label}
        </label>
        <input
            type={type}
            id={name}
            name={name}
            className={`bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 ${inputClassName}`}
            placeholder={placeholder}
            required={isRequired}
            value={value}
            onChange={onChange}
        />
    </div>
  )
}

Input.propTypes = {
  label: PropTypes.string,                    // The label text for the input
  name: PropTypes.string.isRequired,          // The name attribute (required)
  type: PropTypes.oneOf(['text', 'email', 'password', 'number', 'date', 'url']), // Restrict input types
  className: PropTypes.string,                // Extra class for the wrapper div
  inputClassName: PropTypes.string,           // Extra class for the input field
  isRequired: PropTypes.bool,                 // Marks the field as required
  placeholder: PropTypes.string,              // Placeholder text for the input
  value: PropTypes.oneOfType([                // The input value
    PropTypes.string,
    PropTypes.number,
  ]),
  onChange: PropTypes.func,                   // onChange handler function
}

export default Input
