import React, { useState } from 'react';
import Avatar from '../../assets/avatar.svg';
import Input from '../../components/customInput';

// Import images
import Img1 from '../../assets/img1.jpg';
import Img2 from '../../assets/img2.jpg';
import Img3 from '../../assets/img3.jpg';
import Img5 from '../../assets/img5.jpg';
import Img6 from '../../assets/img6.jpg';

const Dashboard = () => {
    const contacts = [
        { name: 'Vinni', status: 'available', img: Img1 },
        { name: 'Chinnu', status: 'available', img: Img5 },
        { name: 'Vaishu', status: 'available', img: Img6 },
        { name: 'Sweety', status: 'available', img: Img2 },
        { name: 'Indu', status: 'available', img: Img3 },
        { name: 'Gulli', status: 'available', img: Img6 }
    ];

    const [user] = useState(JSON.parse(localStorage.getItem('user:detail')));
    const[conversations]=useState
    useEffect(() => {
		const loggedInUser = JSON.parse(localStorage.getItem('user:detail'))
		const fetchConversations = async () => {
			const res = await fetch(`http://localhost:8000/api/conversations/${loggedInUser?.id}`, {
				method: 'GET',
				headers: {
					'Content-Type': 'application/json',
				}
			});
			const resData = await res.json()
			setConversations(resData)
		}
		fetchConversations()
	}, [])

    return (
        <div className='w-screen flex'>
            {/* Sidebar */}
            <div className='w-[25%] border border-black h-screen bg-secondary overflow-y-auto'>
                {/* User Profile */}
                <div className='flex items-center my-4 mx-10'>
                    <img
                        src={Avatar}
                        width={75}
                        height={75}
                        className='border border-primary p-[2px] rounded-full'
                        alt='User Avatar'
                    />
                    <div className='ml-8'>
                        <h3 className='text-2xl'>{user?.fullName}</h3>
                        <p className='text-lg font-light'>My Account</p>
                    </div>
                </div>

                <hr className="border-t border-gray-300 mx-14" />

                {/* Messages Section */}
                <div className='mx-14 mt-10'>
                    <div className='text-primary text-xl mb-4'>Messages</div>
                    {contacts.map(({ name, status, img }) => (
                        <div key={name} className='flex items-center my-6 py-4 border-b border-gray-300'>
                            <div className='cursor-pointer flex items-center'>
                                <img
                                    src={img}
                                    className='w-[60px] h-[60px] border border-primary p-[2px] rounded-full'
                                    alt={name}
                                />
                            </div>
                            <div className='ml-6'>
                                <h3 className='text-lg'>{name}</h3>
                                <p className='text-sm font-light'>{status}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Middle Section */}
            <div className='w-[50%] h-screen bg-white flex flex-col items-center'>
                {/* Header */}
                <div className='w-[75%] bg-secondary h-[80px] my-4 flex items-center px-14 rounded-full shadow-md'>
                    <div className='cursor-pointer'>
                        <img src={Avatar} width={60} height={60} className="rounded-full" alt="Header Avatar" />
                    </div>
                    <div className='ml-6 mr-auto'>
                        <h3 className='text-lg'>John Doe</h3>
                        <p className='text-sm text-gray-600'>online</p>
                    </div>
                </div>

                {/* Content */}
                <div className='h-[75%] w-full overflow-scroll mt-2 border-b'>
                    <div className='h-[10000px] px-10 py-14'>
                        <div className='max-w-[40%] bg-secondary rounded-b-xl rounded-tr-xl p-4 mb-6'>
                            hii there im vinisree from rlykodur
                        </div>
                        <div className='max-w-[40%] bg-primary rounded-b-xl rounded-tl-xl ml-auto p-4 mb-6 text-white'>
                            heyy vinisree this is vaishu from nellore
                        </div>
                    </div>
                </div>

                {/* Input Section */}
                <div className='p-14 w-full flex items-center'>
                    <Input
                        placeholder='Type a message...'
                        className='w-[75%]'
                        inputClassName='p-4 border-0 shadow-md rounded-full bg-light focus:ring-0 focus:border-0 outline-none'
                    />
                    <div className='ml-4 p-2 cursor-pointer bg-light rounded-full'>
                        <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-send" width="30" height="30" viewBox="0 0 24 24" strokeWidth="1.5" stroke="#2c3e50" fill="none" strokeLinecap="round" strokeLinejoin="round">
                            <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                            <line x1="10" y1="14" x2="21" y2="3" />
                            <path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
                        </svg>
                    </div>
                </div>
            </div>

            {/* Right Section */}
            <div className='w-[25%] h-screen bg-light'></div>
        </div>
    );
};

export default Dashboard;
