import Avatar from '../../assets/avatar.svg';

const Dashboard = () => {
    const user = { fullName: 'User Name' }; // Define user to avoid errors

    const contacts = [
        {
            id: 1, // Unique ID for each contact
            name: 'John',
            status: 'available',
            img: Avatar
        },
        {
            id: 2, // Unique ID for each contact
            name: 'Mary',
            status: 'available',
            img: Avatar
        },
        {
            id: 3, // Unique ID for each contact
            name: 'Alexander',
            status: 'available',
            img: Avatar
        }
    ];

    return (
        <div className='w-screen flex'>
            {/* Sidebar */}
            <div className='w-[25%] h-screen bg-secondary overflow-scroll'>
                {/* User Profile */}
                <div className='flex items-center my-8 mx-14'>
                    <div>
                        <img
                            src={Avatar}
                            width={75}
                            height={75}
                            className='border border-primary p-[2px] rounded-full'
                            alt='User Avatar'
                        />
                    </div>
                    <div className='ml-8'>
                        <h3 className='text-2xl'>{user?.fullName}</h3>
                        <p className='text-lg font-light'>My Account</p>
                    </div>
                </div>

                <hr />
                <div className='ml=14 mt=10'>
                    <div className='text-primary text-xl'>Messages</div>
                </div>

                {/* Messages Section */}
                <div>
                    <div className='mx-14 mt-10 text-primary text-lg'>Messages</div>
                    <div>
                        {contacts.map(({ id, name, status, img }) => {
                            return (
                                <div key={id} className='flex items-center my-8 mx-14'>
                                    <div>
                                        <img
                                            src={img}
                                            width={75}
                                            height={75}
                                            className='border border-primary p-[2px] rounded-full'
                                            alt={`${name}'s Avatar`}
                                        />
                                    </div>
                                    <div className='ml-8'>
                                        <h3 className='text-lg'>{name}</h3>
                                        <p className='text-lg font-light'>{status}</p>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
