import Avatar from '../../assets/avatar.svg';
import { useState, useRef } from 'react';
import Input from '../../components/Input'; // Assuming this exists

const Dashboard = () => {
    const [messages, setMessages] = useState(null); // Example state for messages
    const [message, setMessage] = useState('');
    const messageRef = useRef();

    const user = { fullName: 'User Name' }; // Define user to avoid errors

    const contacts = [
        { id: 1, name: 'John', status: 'available', img: Avatar },
        { id: 2, name: 'Mary', status: 'available', img: Avatar },
        { id: 3, name: 'Alexander', status: 'available', img: Avatar },
        { id: 4, name: 'Jossy', status: 'available', img: Avatar },
        { id: 5, name: 'Larry', status: 'available', img: Avatar },
        { id: 6, name: 'Adam', status: 'available', img: Avatar }
    ];

    // Function to send message (you can replace this with your actual logic)
    const sendMessage = () => {
        console.log('Sending message:', message);
        setMessages((prevMessages) => ({
            ...prevMessages,
            messages: [...(prevMessages?.messages || []), { message, user: { id: 1 } }] // Add message to the messages list
        }));
        setMessage('');
    };

    return (
        <div className='w-screen flex'>
            {/* Sidebar */}
            <div className='w-[25%] h-screen bg-secondary overflow-scroll'>
                {/* User Profile */}
                <div className='flex items-center my-8 mx-14'>
                    <div>
                        <img
                            src={Avatar}
                            width={75}
                            height={75}
                            className='border border-primary p-[2px] rounded-full'
                            alt='User Avatar'
                        />
                    </div>
                    <div className='ml-8'>
                        <h3 className='text-2xl'>{user?.fullName}</h3>
                        <p className='text-lg font-light'>My Account</p>
                    </div>
                </div>

                <hr />
                <div className='ml-14 mt-10'>
                    <div className='text-primary text-xl'>Messages</div>
                </div>

                {/* Messages Section */}
                <div className='mx-14 mt-10'>
                    <div className='text-primary text-lg'>Contacts</div>
                    <div>
                        {contacts.map(({ id, name, status, img }) => (
                            <div key={id} className='flex items-center my-8 py-8 border-b-gray-300'>
                                <div className='cursor-pointer flex items-center'>
                                    <img
                                        src={img}
                                        width={75}
                                        height={75}
                                        className='border border-primary p-[2px] rounded-full'
                                        alt={`${name}'s Avatar`}
                                    />
                                </div>
                                <div className='ml-8'>
                                    <h3 className='text-lg'>{name}</h3>
                                    <p className='text-lg font-light'>{status}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Main Chat Section */}
            <div className='w-[50%] h-screen bg-white flex flex-col items-center'>
                {/* Message Header */}
                /{messages?.receiver?.fullName && (
                    <div className='w-[75%] bg-secondary h-[80px] my-14 rounded-full flex items-center px-14 py-2'>
                        <div className='cursor-pointer'>
                            <img src={Avatar} width={60} height={60} className="rounded-full" alt='Receiver Avatar' />
                        </div>
                        <div className='ml-6 mr-auto'>
                            <h3 className='text-lg'>{messages?.receiver?.fullName}</h3>
                            <p className='text-sm font-light text-gray-600'>{messages?.receiver?.email}</p>
                        </div>
                        <div className='cursor-pointer'>
                            {/* Call icon */}
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-phone-outgoing" width="24" height="24" viewBox="0 0 24 24" strokeWidth="1.5" stroke="black" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                                <path d="M5 4h4l2 5l-2.5 1.5a11 11 0 0 0 5 5l1.5 -2.5l5 2v4a2 2 0 0 1 -2 2a16 16 0 0 1 -15 -15a2 2 0 0 1 2 -2" />
                                <line x1="15" y1="9" x2="20" y2="4" />
                                <polyline points="16 4 20 4 20 8" />
                            </svg>
                        </div>
                    </div>
                )}

                {/* Chat Messages */}
                <div className='h-[75%] w-full overflow-scroll shadow-sm'>
                    <div className='p-14'>
                        {messages?.messages?.length > 0 ? (
                            messages.messages.map(({ message, user: { id } = {} }) => (
                                <div
                                    key={id}
                                    className={`max-w-[40%] rounded-b-xl p-4 mb-6 ${
                                        id === user?.id
                                            ? 'bg-primary text-white rounded-tl-xl ml-auto'
                                            : 'bg-secondary rounded-tr-xl'
                                    }`}
                                >
                                    {message}
                                </div>
                            ))
                        ) : (
                            <div className='text-center text-lg font-semibold mt-24'>No Messages or No Conversation Selected</div>
                        )}
                    </div>
                </div>

                {/* Message Input Section */}
                {messages?.receiver?.fullName && (
                    <div className='p-14 w-full flex items-center'>
                        <Input
                            placeholder='Type a message...'
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            className='w-[75%]'
                            inputClassName='p-4 border-0 shadow-md rounded-full bg-light focus:ring-0 focus:border-0 outline-none'
                        />
                        <div
                            className={`ml-4 p-2 cursor-pointer bg-light rounded-full ${!message && 'pointer-events-none'}`}
                            onClick={sendMessage}
                        >
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-send" width="30" height="30" viewBox="0 0 24 24" strokeWidth="1.5" stroke="#2c3e50" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                                <line x1="10" y1="14" x2="21" y2="3" />
                                <path d="M21 3l-6.5 18a0.55 .55 0 0 1 -1 0l-3.5 -7l-7 -3.5a0.55 .55 0 0 1 0 -1l18 -6.5" />
                            </svg>
                        </div>
                        <div className={`ml-4 p-2 cursor-pointer bg-light rounded-full ${!message && 'pointer-events-none'}`}>
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-circle-plus" width="30" height="30" viewBox="0 0 24 24" strokeWidth="1.5" stroke="#2c3e50" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" fill="none" />
                                <circle cx="12" cy="12" r="9" />
                                <line x1="9" y1="12" x2="15" y2="12" />
                                <line x1="12" y1="9" x2="12" y2="15" />
                            </svg>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
